import React from "react";

export default class Profile extends React.Component
{
    constructor()
    {
        super();
        this.state =  {
            name: "Shubham",
            email: "shubham@test.com",
            count: 0
        }
    }
    updateState()
    {
        this.setState({
            name: "Shubham : The Web Developer",
            email: "shubhams@techis.io",
            count: this.state.count+1
        })
    }
    render ()
    {
        return (
            <>
            <h1>Hello {this.state.name}</h1>
            <h1>{this.state.email}</h1>
            <button onClick={()=>{this.updateState()}}>Update Name & Email</button>
            </>
        )
    }
}
